void ScrollArea::SetScrollArea(QWidget *widget, QString text)
{
  QVBoxLayout *layout = new QVBoxLayout(widget);
  QLabel *lab1e = new QLabel(text);
  lab1e->setStyleSheet("QLabel{background:rgb(30, 30, 30);;font:36px;color:rgb(255, 255, 255);}");
  lab1e->setAlignment(Qt::AlignCenter);
  layout->addWidget(lab1e);
  widget->setLayout(layout);
}
